import React, { Fragment } from "react";
// Customizable Area Start
import './css/index.css';
import { Formik, Form, Field } from 'formik';
import { Button, FormGroup } from 'reactstrap';
import { withRouter, Link } from 'react-router-dom';
import { FaFacebookF, FaRegEnvelope, FaEye, FaEyeSlash } from 'react-icons/fa';
import { CgSpinner } from 'react-icons/cg';
import firebase from 'firebase';
import * as Yup from "yup";
import axios from "axios";
// @ts-ignore
import content from "../../../../components/src/content.js"
//@ts-ignore
import map from "lodash/map"
// Customizable Area End

import EmailAccountLoginController, {
  Props
} from "./EmailAccountLoginController.web";
import Loader from "../../../../components/src/Loader.web";



const SigninSchema = Yup.object().shape({
  userEmail: Yup.string()
    .email('Please enter a valid email address.')
    .required('Email is required.'),
  password: Yup
    .string()
    .min(8, 'Minimum Password length is 8.')
    .max(16, 'Maximum Password length is 16')
    .matches(
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
      'Password must contain atleast a capital letter, a lowercase letter, a number and a special character.'
    )
    .required('Password is required.')
});

class EmailAccountLoginBlock extends EmailAccountLoginController {

  // Customizable Area Start
  // Customizable Area End

  constructor(props: Props) {
    super(props);
    // Customizable Area Start
    // Customizable Area End
  }

  signinUser = async (values: any) => {
    this.setShowSpinner(true);
    let data;
    //@ts-ignore
    const guestuserID = localStorage.getItem('guestUUID');
    if (guestuserID) {
      data = {
        email_or_mobile: values.userEmail,
        password: values.password,
        uuid: guestuserID
      }
    }
    else {
      data = {
        email_or_mobile: values.userEmail,
        password: values.password
      }
    }
    return this.handleSubmitLogin(data);

    axios.post("https://ecommerceapp-39394-ruby.39394.dev.ap-southeast-1.aws.svc.builder.ai/bx_block_login/login/", data)
      .then((res: any) => {
        if (res.data) {
          const { data, meta } = res.data;
          if (meta && meta.token) {
            localStorage.setItem("token", meta.token)
          }
          if (data) {
            localStorage.setItem("user", JSON.stringify(data));
            let dat = {
              'email': data.attributes.email,
              'name': data.attributes.full_name
            }
            localStorage.setItem('userData', JSON.stringify(dat));
            localStorage.setItem('profileImage', data.attributes.image_url && data.attributes.image_url);
            const GuesrUserId = localStorage.getItem('guestUUID');
            const GuestUserData = localStorage.getItem('guestUserData');
            if (GuestUserData && guestuserID) {
              //@ts-ignore
              this.props?.location?.state?.redirect === 'wishlist'
                ?
                setTimeout(() => {
                  this.props.history?.push({ pathname: '/profilebio', state: { activeTab: "wishlist" } })
                }, 1000)
                :
                // @ts-ignore
                this.routeToAll("/cart");
            }
            else {
              //@ts-ignore
              window.notify([{ type: 'success', message: meta?.message }])
              // @ts-ignore
              this.routeToAll("/home-page");
            }
          }
        }
      })
      .catch((error: any) => {
        if (error?.request?.response && error?.request?.status !== 0) {
          let response = JSON.parse(error.request.response);
          map(response?.errors, (errorMesage: any) => {
            map(errorMesage, (val: any, key: any) => {
              console.log(key)
              // @ts-ignore
              window.notify([{ message: val || "Something went wrong!!!", type: "danger" }]);
            })
          })
        }
        console.error(error);
      })
      .finally(() => {
        this.setShowSpinner(false);
      })
  };

  //connect to google account
  connectGoogle = () => {
    const googleResult = new firebase.auth.GoogleAuthProvider();
    googleResult.addScope('profile');
    googleResult.addScope('email');
    firebase.auth().signInWithPopup(googleResult)
      .then(result => {
        console.log(result, "google result");
        if (result.credential && result.user && result.additionalUserInfo) {
          this.setState({
            ...this.state,
            socialMediaResult: result
          });
          this.verifyEmailBeforeRegistartion(result, 'google');
        }
      }).catch(err => {
        console.log("Googleerrr", err);
        // @ts-ignore
        window.notify([{ message: 'Error Occured While connceting to Google' || "Something went wrong!!!", type: "danger" }]);
      });
  };
  //connect to facebook account
  connectFacebook = () => {
    const facebookResult = new firebase.auth.FacebookAuthProvider();
    facebookResult.addScope('email');
    // facebookResult.setCustomParameters({
    //   'display': 'popup'
    // })
    firebase.auth().signInWithPopup(facebookResult).then(result => {
      console.log(result, "facebook result");
      if (result.credential && result.user && result.additionalUserInfo) {
        this.setState({
          ...this.state,
          socialMediaResult: result
