import { defineFeature, loadFeature} from "jest-cucumber"
import { shallow, ShallowWrapper } from 'enzyme'

import * as helpers from '../../../../framework/src/Helpers'
// import {runEngine} from '../../../../framework/src/RunEngine'
// import {Message} from "../../../../framework/src/Message"

// import MessageEnum, {getName} from "../../../../framework/src/Messages/MessageEnum"; 
import React from "react";
import Filteritems from "../../src/Filteritems"
const navigation = require("react-navigation")

const screenProps = {
    navigation: navigation,
    id: "Filteritems"
  }

const feature = loadFeature('./__tests__/features/filteritems-scenario.feature');

defineFeature(feature, (test) => {


    beforeEach(() => {
        jest.resetModules()
        jest.doMock('react-native', () => ({ Platform: { OS: 'web' }}))
        jest.spyOn(helpers, 'getOS').mockImplementation(() => 'web');
    });

    test('User navigates to filteritems', ({ given, when, then }) => {
        let filteritemsWrapper:ShallowWrapper;
        let instance:Filteritems; 

        given('I am a User loading filteritems', () => {
            filteritemsWrapper = shallow(<Filteritems {...screenProps}/>)
            expect(filteritemsWrapper).toBeTruthy();
            instance = filteritemsWrapper.instance() as Filteritems;
        });

        when('I navigate to the filteritems', () => {
             instance = filteritemsWrapper.instance() as Filteritems
        });

        then('filteritems will load with out errors', () => {
            expect(filteritemsWrapper).toBeTruthy()
        });

        then('I can enter text with out errors', () => {
            let textInputComponent = filteritemsWrapper.findWhere(
                node => node.prop("testID") === "filterBrand"
              );
    
              textInputComponent = filteritemsWrapper.findWhere(
                node => node.prop("testID") === "filterCategory"
              );
        
        
        });

        then('I can select the button with with out errors', () => {
              let buttonComponent = filteritemsWrapper.findWhere((node) => node.prop('testID') === 'navigateToFilter');
              expect(filteritemsWrapper).toBeTruthy();
              buttonComponent = filteritemsWrapper.findWhere((node) => node.prop('testID') === 'openFilterPrice');
              buttonComponent = filteritemsWrapper.findWhere((node) => node.prop('testID') === 'openFilterBrand');
              buttonComponent = filteritemsWrapper.findWhere((node) => node.prop('testID') === 'openFiltercategory');
              buttonComponent = filteritemsWrapper.findWhere((node) => node.prop('testID') === 'BackPage');
              buttonComponent = filteritemsWrapper.findWhere((node) => node.prop('testID') === 'applyFilter');
        });

        then('I can leave the screen with out errors', () => {
            instance.componentWillUnmount()
            expect(filteritemsWrapper).toBeTruthy()
        });
    });


});
