import { defineFeature, loadFeature} from "jest-cucumber"
import { shallow, ShallowWrapper } from 'enzyme'

import * as helpers from '../../../../framework/src/Helpers'
import {runEngine} from '../../../../framework/src/RunEngine'
import {Message} from "../../../../framework/src/Message"

import MessageEnum, {getName} from "../../../../framework/src/Messages/MessageEnum"; 
import React from "react";
import Orderdetailview from "../../src/Orderdetailview"
const navigation = require("react-navigation")

const screenProps = {
    navigation: navigation,
    id: "Orderdetailview"
  }

const feature = loadFeature('./__tests__/features/orderdetailview-scenario.feature');

defineFeature(feature, (test) => {


    beforeEach(() => {
        jest.resetModules()
        jest.doMock('react-native', () => ({ Platform: { OS: 'web' }}))
        jest.spyOn(helpers, 'getOS').mockImplementation(() => 'web');
    });

    test('User navigates to orderdetailview', ({ given, when, then }) => {
        let orderdetailviewWrapper:ShallowWrapper;
        let instance:Orderdetailview; 

        given('I am a User loading orderdetailview', () => {
            orderdetailviewWrapper = shallow(<Orderdetailview {...screenProps}/>)
        });

        when('I navigate to the orderdetailview', () => {
             instance = orderdetailviewWrapper.instance() as Orderdetailview
        });

        then('orderdetailview will load with out errors', () => {
            expect(orderdetailviewWrapper).toBeTruthy()
        });

        then('I can leave the screen with out errors', () => {
            instance.componentWillUnmount()
            expect(orderdetailviewWrapper).toBeTruthy()
        });
    });


});
